mapper.select:
/* selectCommCodeGroupPageCount - 공통코드그룹 건수 조회 */

select count(*)
  from tbl_comm_code_group


    and code_group_id = #{codeGroupId}


    and use_yn = #{useYn}


    and del_yn = #{delYn}


    and code_group_nm like concat('%', #{codeGroupNm},'%')

/* selectCommCodeGroupListPage - 공통코드그룹 목록 조회 */

select grp.code_group_id
     , grp.code_group_nm
     , grp.code_group_dc
     , grp.use_yn
     , grp.del_yn
  from tbl_comm_code_group grp
 inner join ( select code_group_id
                from tbl_comm_code_group


                   and code_group_id = #{codeGroupId}


                   and use_yn = #{useYn}


                   and del_yn = #{delYn}


                   and code_group_nm like concat('%', #{codeGroupNm},'%')


              limit #{firstRecordIndex}, #{recordCountPerPage}
             ) grp_pk
             using(code_group_id)

/* selectCommCode - 공통코드 조회 */
SELECT code_group_id , code_id , code_nm , code_dc , use_yn , del_yn , reg_dt , reg_user_id , upd_dt , upd_user_id FROM tbl_comm_code WHERE code_group_id = #{codeGroupId}
